import { map } from 'rxjs/operators';
import * as i0 from "@angular/core";
import * as i1 from "@angular/fire/compat/firestore";
export class DataService {
    constructor(afs) {
        this.afs = afs;
        this.invitadoCollection = afs.collection('personas');
        this.grupoCollection = afs.collection('grupos');
        this.getInvitados();
        this.getGrupos();
    }
    addInvitado(invitado) {
        return new Promise(async (resolve, reject) => {
            try {
                invitado.id = invitado.id || this.afs.createId();
                const result = await this.invitadoCollection.doc(invitado.id).set(invitado);
                resolve(result);
            }
            catch (err) {
                reject(err);
            }
        });
    }
    async getInvitados() {
        this.invitados = await this.invitadoCollection
            .snapshotChanges()
            .pipe(map((actions) => actions.map((a) => a.payload.doc.data())));
    }
    deleteInvitado(id) {
        return new Promise(async (resolve, reject) => {
            try {
                const result = await this.invitadoCollection.doc(id).delete();
                resolve(result);
            }
            catch (err) {
                reject(err);
            }
        });
    }
    addGrupo(grupo) {
        return new Promise(async (resolve, reject) => {
            try {
                grupo.id = grupo.id || this.afs.createId();
                const result = await this.grupoCollection.doc(grupo.id).set(grupo);
                resolve(result);
            }
            catch (err) {
                reject(err);
            }
        });
    }
    async getGrupos() {
        this.grupos = await this.grupoCollection
            .snapshotChanges()
            .pipe(map((actions) => actions.map((a) => a.payload.doc.data())));
    }
}
