import { map } from 'rxjs/operators';
import * as i0 from "@angular/core";
import * as i1 from "@angular/fire/compat/firestore";
export class DataService {
    constructor(afs) {
        this.afs = afs;
        this.invitadoCollection = afs.collection('personas');
        this.getInvitados();
    }
    addInvitado(invitado, invitadoId) {
        return new Promise(async (resolve, reject) => {
            try {
                const id = invitadoId || this.afs.createId();
                const data = Object.assign({ id }, invitado);
                const result = await this.invitadoCollection.doc(id).set(data);
                resolve(result);
            }
            catch (err) {
                reject(err);
            }
        });
    }
    async getInvitados() {
        this.invitados = await this.invitadoCollection
            .snapshotChanges()
            .pipe(map((actions) => actions.map((a) => a.payload.doc.data())));
    }
    deleteInvitado(id) {
        return new Promise(async (resolve, reject) => {
            try {
                const result = await this.invitadoCollection.doc(id).delete();
                resolve(result);
            }
            catch (err) {
                reject(err);
            }
        });
    }
}
