import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { map, startWith } from 'rxjs/operators';
let HomeComponent = class HomeComponent {
    constructor(dataService, router) {
        this.dataService = dataService;
        this.router = router;
        this.myControl = new FormControl();
        this.options = [];
        this.numInvitados = 0;
        this.numInvitadosHombre = 0;
        this.numInvitadosMujer = 0;
        this.numInvitadosEnviado = 0;
        this.numInvitadosConfirmado = 0;
        this.navExtras = {
            state: {
                persona: null
            }
        };
        this.typesOfShoes = ['Boots', 'Clogs', 'Loafers', 'Moccasins', 'Sneakers'];
        this.columns = [
            {
                columnDef: 'parentesco',
                header: 'Parentesco',
                cell: (element) => `${element.parentesco}`
            },
            {
                columnDef: 'nombre',
                header: 'Nombre',
                cell: (element) => `${element.nombre} ${element.apellidos}`
            },
            {
                columnDef: 'invitado',
                header: 'Invitado',
                cell: (element) => element.invitado == true ? `SI` : ""
            },
            {
                columnDef: 'confirmado',
                header: 'Confirmado',
                cell: (element) => element.confirmado == true ? `SI` : ""
            }
        ];
    }
    async ngOnInit() {
        await this.getInvitados();
    }
    async getInvitados() {
        await this.dataService.invitados.subscribe((invitados) => {
            this.invitados = invitados;
            this.numInvitados = invitados.length;
            this.numInvitadosHombre = invitados.filter(x => x.sexo == "Hombre").length;
            this.numInvitadosMujer = invitados.filter(x => x.sexo == "Mujer").length;
            this.numInvitadosEnviado = invitados.filter(x => x.invitado == true).length;
            this.numInvitadosConfirmado = invitados.filter(x => x.confirmado == true).length;
            this.options = invitados;
            this.filteredOptions = this.myControl.valueChanges.pipe(startWith(''), map(value => this._filter(value)));
            this.fillTable(invitados);
        });
    }
    goToNew() {
        this.router.navigate(['new']);
    }
    goToEdit(persona) {
        if (this.navExtras.state)
            this.navExtras.state = persona;
        this.router.navigate(['edit'], this.navExtras);
    }
    onDelete(id) {
        this.dataService.deleteInvitado(id);
    }
    fillTable(ELEMENT_DATA) {
        this.displayedColumns = this.columns.map(c => c.columnDef);
    }
    _filter(value) {
        const filterValue = value.toLowerCase();
        return this.invitados.filter(option => option.nombre.toLowerCase().includes(filterValue));
    }
    filterInvitados(e) {
        console.log(e);
        this.invitados = this.invitados.filter(x => x.id === e);
    }
};
HomeComponent = __decorate([
    Component({
        selector: 'app-home',
        templateUrl: './home.component.html',
        styleUrls: ['./home.component.scss']
    })
], HomeComponent);
export { HomeComponent };
